            tmp.setCyclicData(t);


            MyData k = new MyData();
            k.setId(i);
            k.setName("ming" + i);
            k.setAge(BigDecimal.valueOf(i));

            resultMap.put(k, tmp);
        }
        return resultMap;
    }


